mapper.insert:
	    
	    	SELECT JARYO_SEQ.NEXTVAL FROM DUAL
	    
	    INSERT INTO jaryo(    BNO 
	   					       , TITLE 
	   					       , CONTENT 
	   					       , WRITER  )
	                  VALUES(    #{bno}
	                 		   , #{title}
	                 		   , #{content}
	                 		   , #{writer}  )

mapper.update:
UPDATE jaryo
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}
